vec3 l3d(float t, vec3 d, vec3 w) //lissajous curve
{
    vec3 pos;
    pos.x = sin(w.x*t + d.x);
    pos.y = sin(w.y*t + d.y);
    pos.z = sin(w.z*t + d.z);
    return pos;
}

float bg (vec2 uv) //pseudorandom noise, raggedy fractional part of sin(x)
{
    return fract(sin(length(uv)*(sin(0.005*iTime)+1.)*1000.));
}

float cheapSigmoid(float start, float end, float x)
{
  x = clamp((x - start) / (end - start), 0.0, 1.0); 
  return x * x * (3. - 2. * x);
}

vec3 blip(vec2 uv, vec3 pos, float r) //fish component? kind of? 
{
    uv -= .5;
    uv -= vec2(pos.x, pos.y);
    uv.x *= iResolution.x/iResolution.y;
    float d = length(uv);
    vec3 c = vec3(cheapSigmoid(r, r+0.02, d)) * vec3(1.1,1.1,1.) + vec3(.06,.05,.005)/20.;

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;  

    vec3 c = vec3(1.);    

    //fish

    for(int i =0; i < 9; i++)
    {
        vec3 pos = l3d(iTime-float(i)/49.0, vec3(5,2,4-i), vec3(7,9,2)/5.);
        c*= blip(uv, pos/2.0, ((pos.z+1.0)/2.0)/30.0);
    }
     for(int i =0; i < 9; i++)
    {
        vec3 pos = l3d(iTime-float(i)/49.0, vec3(5,2,4-i), vec3(5,4,3)/2.);
        c*= blip(uv, pos/2., ((pos.z+1.0)/2.0)/30.0);
    }
      for(int i =0; i < 9; i++)
    {
        vec3 pos = l3d(iTime-float(i)/49.0, vec3(5,2,4-i), vec3(3,7,3)/2.);
        c*= blip(uv, pos/2., ((pos.z+1.0)/2.0)/30.0);
    }
      for(int i =0; i < 9; i++)
    {
        vec3 pos = l3d(iTime-float(i)/49.0, vec3(5,2,4-i), vec3(9,3,5)/2.8);
        c*= blip(uv, pos/2., ((pos.z+1.0)/2.0)/30.0);
    }

      for(int i =0; i < 10; i++)
    {
        vec3 pos = l3d(iTime-float(i)/49.0, vec3(5,2,4-i), vec3(2,3,5)/1.1);
        c*= blip(uv, pos/2., ((pos.z+1.0)/2.0)/30.0);
    }



    uv -= vec2(.5,.5);

    if(length(c) > 10.)
    {
        c = vec3(bg(uv),bg(uv),bg(uv))/4.;

    }

    c = 1.-c;

    fragColor = vec4(c,1.0);
}
