vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}
vec2 rot(vec2 uv,float a){
	return vec2(uv.x*cos(a)-uv.y*sin(a),uv.y*cos(a)+uv.x*sin(a));
}

vec2 fractalize(vec2 uv,int maxIterations){

	float s=0.3;
	for(int i=0;i<maxIterations;i++){
		uv=abs(uv)-s;
		uv=rot(uv,iTime);
		s=s/2.1;
	}
    return uv;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 inuv = fragCoord.xy/iResolution.xy;
    vec3 col = vec3(0);

    uv *= 2.;
    uv=fract(uv)-.5;

    uv = fractalize(uv,5);

    col = pal( (uv.x+uv.y)*20., vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );

    col.rgb = texture(iChannel0,inuv).rgb + col*0.1;

    fragColor = vec4(col,1.0);
}
