void mainImage(out vec4 fragColor, vec2 fragCoord)
{   
    float gray = 0.0;
    float realGray = 0.0;
    vec4 finalColor = texture(iChannel0, fragCoord / iResolution.xy);

    // Average
    // gray = (finalColor.r + finalColor.g + finalColor.b) / 3.0;

    // Real lumen calculation
    realGray = sqrt(dot(finalColor*finalColor , vec4(0.299, 0.587, 0.114, 0)));

    // fragColor = vec4(vec3(gray), finalColor.a);
    fragColor = vec4(mix(vec3(realGray), finalColor.xyz, abs(sin(iTime * 1.0))), finalColor.a);
}
