void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.y;
    float scale = 10.00/iMouse.x;
    if(scale>3.00){scale=3.00;}
    float cx = -1.782/scale;//-10.00+((iMouse.x)/iResolution.x);
    float cy = -0.50;//+((iMouse.y)/iResolution.y);
    float limit = 4.00;
    float ax=(cx+uv.x)*scale;
    float ay=(cy+uv.y)*scale;
    float a1=ax;
    float b1=ay;
    int lp=0;
    for(int i=0;i<255;++i){
        float a2=(a1*a1)-(b1*b1)+ax;
        float b2=2.0*a1*b1+ay;
        a1=a2;
        b1=b2;
        lp=i;
        if(((a1*a1)+(b1*b1))>limit){break;}
    }
    vec3 col = vec3(a1*b1,a1*b1,a1*b1);
    if(lp==254){
        col = vec3(sin(((a1*a1)/(b1*b1))/(iMouse.y/3.00)), sin((a1/b1)/(iMouse.y/5.00)),sin(((b1/a1)/(a1/b1))/(iMouse.y/7.00)));
    }
    fragColor = vec4(col,1.0);
}
