float lums(vec3 rgb)
{
    const vec3 W = vec3(0.299, 0.587, 0.114);
    return dot(rgb, W);
}

vec3 gradient(float t) {
    const vec3 COLOR1 = vec3(0.788, 0.972, 0.952);
    const vec3 COLOR2 = vec3(0.972, 0.792, 0.843);

    // Increasing the range of t to [0, 2pi], then taking cosine to get reversed gradient
    return mix(COLOR1, COLOR2, cos(2.0 * 3.14 * t));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 chan0color = texture(iChannel0, uv.xy);
    float c0_lum = lums(chan0color.rbg);

    fragColor.rgb = gradient(mod(c0_lum + iTime / 7.0, 1.0));
}
