#define EFFECT_1 0
#define EFFECT_2 1

#define COLOR_MODE_DEFAULT		0
#define COLOR_MODE_GRAYSCALE	1

#define EFFECT_TYPE EFFECT_1
#define COLOR_MODE	COLOR_MODE_DEFAULT

const float Speed = 0.6;

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
	uv.x += cos(iTime * 0.1);      
    uv.x = mix(uv.x, 0.25, uv.x);

    vec3 influence = texture(iChannel0, uv).rgb * vec3(0.85, 0.5, 0.5);
    float influenceSum = influence.r + influence.g + influence.b;
    if (influenceSum > 0.1)
    {
        #if   EFFECT_TYPE == EFFECT_1
        uv.y += sin(uv.x * 40.0 + (iTime * Speed)) * 0.005;
        uv.x += cos(uv.y * 40.0 + (iTime * Speed)) * 0.01;
        uv.x += uv.y * 0.01;
        #elif EFFECT_TYPE == EFFECT_2
        uv.y += sin(uv.x * 60.0 + (iTime * Speed)) * 0.005;
        uv.x += cos(uv.y * 60.0 + (iTime * Speed)) * 0.001;
        uv.x += uv.y * 0.01;
        #endif
    }

    vec3 col = texture(iChannel1, uv).rgb * influence; 
    if (uv.y < 0.18)
    {
        col += texture(iChannel2, uv).rgb * 0.5;
    }

    #if COLOR_MODE == COLOR_MODE_GRAYSCALE
    float luminance = col.r * 0.2126 + col.g * 0.7152 + col.b * 0.0722;
    col = vec3(luminance);
    #endif

    fragColor = vec4(col,1.0);
}
