void mainImage( out vec4 fragColor, in vec2 fragCoord )  {
  vec3 c = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;
  float v = dot(c, c);

  vec2 p = fragCoord.xy;
  vec2 p1 = fragCoord.xy;
  vec2 p2 = fragCoord.xy;
  vec2 p3 = fragCoord.xy;


  p = mod(vec2(p.x, p.y), vec2(16.0));
  p1 = mod(vec2(p.x, p.y), vec2(8.0));
  p2 = mod(vec2(p.x, p.y), vec2(4.0));
  p3 = mod(vec2(p.x, p.y), vec2(2.0));

  fragColor = vec4(vec3(
      float((v > 0.85)  || (floor(p.x) != 0.0 || floor(p.y) != 0.0)) *
  	  float((v > 0.75) || (floor(p1.x) != 0.0 || floor(p1.y) != 0.0)) *
      float((v > 0.60) || (floor(p2.x) != 0.0 || floor(p2.y) != 0.0)) *
      float((v > 0.25) || (floor(p3.x) != 0.0 || floor(p3.y) != 0.0))

  ), 1.0);

}
