void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
	vec2 st = vec2(atan(uv.x,uv.y),length(uv));
    uv = 4.*vec2(st.x/6.2831+.518,st.y*1.2);
    float p = -((uv.y*0.6-0.62831)*0.5*sin(uv.x*.518))-0.3*cos(100.*uv.x);
    vec3 c = -uv.y+p + 0.62381*cos(uv.yyy+0.6*sin(iTime+11.*uv.x)*vec3(2));
    vec3 col = c.y*(sin(c.x)+.5)*vec3(uv.y,1.,c.x);

    // Output to screen
    fragColor = vec4(col,1.0);
}
