float makeBand(float d, float start, float end, float blur)
{
    float band1 = smoothstep(start - blur, start + blur, d);
    float band2 = smoothstep(end + blur, end - blur, d);
    return band1*band2;
}

float makeRect(vec2 uv, float left, float right, float top, float bottom, float blur)
{
    float verticalBand = makeBand(uv.x ,left,right,blur );
    float horizontalBand = makeBand(uv.y, bottom, top, blur);
    return verticalBand * horizontalBand;
}

float makeDiamond(vec2 uv, float left, float right, float top, float bottom, float blur)
{
    float verticalBand = makeBand(uv.x +uv.y ,left,right,blur );
    float horizontalBand = makeBand(uv.y - uv.x, bottom, top, blur);
    return verticalBand * horizontalBand;
}

float remap1(float a, float b, float t) //remap t to a - b in a range of 0-1 
{
 	return ((t-a) / (b - a));
}

float remap2(float a , float b, float c, float d , float t)
{
    return remap1(a,b,t) * (d-c) + c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5f;
    uv.x *= iResolution.x / iResolution.y;
    uv*=3.0f;

    float x = uv.x;
    float y = uv.y;
    float m = -(x-.5f)*(x+.5f);
    m = 0.4f*sin(x*8.0f + iTime);
    y -= m;
    float blur;
    blur += remap2(-.9f,.9f, 0.02f,0.2f,x);
    float normalRect = makeRect(vec2(x,y),-.9f,.9f,.2f,-.2f,blur);
    float rectMask = makeRect(vec2(x,y), -.2f * abs(sin(iTime)) , .2f* abs(sin(iTime)) ,.2f * abs(sin(iTime)),-.2f* abs(sin(iTime)), .002f);

    vec3 col =vec3( .0f);


    col += normalRect;

    fragColor = vec4(col,1.f); 


}
