renderpass.code:
float cir(vec2 p, float r)
{
    return smoothstep(3./iResolution.y,0.,length(p)-r);
}

float sq(vec2 p, float d)
{
    p=smoothstep(3./iResolution.y,.0,abs(p)-d-.1);
    return  p.x*p.y;
}

float ghost(vec2 p)
{
    float f = sq((p+vec2(0,.3))/vec2(1,.7),.39)+cir(p,.5);
    if(p.y+.5<(cos(floor(iTime*5.)+p.x*20.)/15.+p.x*p.x/1.5))return 0.;
    else return clamp(f,0.,1.);
}


void mainImage( out vec4 c, in vec2 f )
{
    vec2 buv = f.xy/iResolution.xy;
    vec2 uv = ((2.*f-iResolution.xy)/iResolution.y)-vec2(0,.1);
    vec3 col = vec3(ghost(uv))-
        3.*cir(uv+vec2(0.2,-.2+.01*cos(floor(iTime*10.))),.1)-
        3.*cir(uv+vec2(-0.2,-.2+.01*cos(floor(iTime*10.))),.1);
	vec3 oc = texture(iChannel0, buv - vec2(0.0, -3.0/iChannelResolution[0].y*2.)).rgb;
    float t = 8.2+cos(iTime)*.2;
    c.rgb=col+(vec3(1.)*oc/1.3)+vec3(cos(t),sin(t*2.),cos(t*2.)/2.)*.5;
}
