float width = 5.0;
float height = 5.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )

{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio = iResolution.x/iResolution.y;
    float squareRatio = width/height;

    uv.x *= aspectRatio * width;
    uv.y *= squareRatio * height;

    vec4 display;

    //textures for squares
    vec4 whiteSquare = texture(iChannel0, uv);
    vec4 blackSquare = texture(iChannel1, uv); 

    //calculate rows and cols
    float interpX = step(1.0, mod(uv.x, 2.0));
    float interpY = step(1.0, mod(uv.y, 2.0));


    float numTiles = interpX + interpY;

    display = mix(whiteSquare, blackSquare, mod(numTiles, 2.0)) * step(uv.x, floor(width * aspectRatio));

    // Output to screen
    fragColor = display;

}
