const float speed1 = 0.5;
const float factor1 = 0.05;
const float base1 = 1.0;

const float speed2 = 0.1;
const float factor2 = 0.1;
const float base2 = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 one = vec2(sin(iTime*speed1+0.2)*factor1+base1, cos(iTime*speed1-0.2)*factor1+base1);
	vec2 two = vec2(cos(iTime*speed2)*factor2+base2, sin(iTime*speed2)*factor2+base2);

    vec2 t = uv + two;
    for (int i = 0; i < 10; i++) {
        vec2 temp = t;
        temp.x = sin(t.x) + t.y * one.x;
        temp.y = cos(t.y) * t.x * one.y;
        t = temp;
        temp.x = cos(t.x) + t.y * one.x / one.y;
        temp.y = sin(t.y) * t.x * one.y / one.x;
        t = temp;
    }
    t = normalize(t);
    vec3 color = vec3(t.x, t.y, 0.0);

    fragColor = vec4(color, 0.0);

}
