float R = .35;
float pi = 3.14;

float step2(float x){
	return x*x;
}

vec2 decToPolar(vec2 xy){
    float x = xy.x; float y = xy.y;

    float r = sqrt(step2(x) + step2(y));
    float fi = 0.;

	if (x > 0.){
        if (y > 0.){fi = atan(y/x);}
        else if (y < 0.){fi = atan(y/x) + 2.*pi;}
        else if (y == 0.){fi = 0.;};
    }
    else if (x < 0.){
        fi = atan(y/x) + pi;
    }
    else if (x == 0.){
        if (y > 0.){fi = pi/2.;}
        else if (y < 0.){fi = 3.*pi/2.;}
        else if (y == 0.){fi = 0.;}
    }

    return vec2(r, fi);
}

vec2 polarToDec(vec2 rf){
	return vec2 (rf.x * cos(rf.y), rf.x * sin(rf.y));
}


vec2 vortex(vec2 rf){
    float delFi = pi*(1. - rf.x / R);
    rf.y -= delFi;
    return polarToDec(rf);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    R = R * iResolution.y;
    vec2 cen = vec2(iResolution.x*(0.5 + (distance(fract(iTime/2.), 0.5)-0.25)*1.8),
        			iResolution.y*(0.5 + sin(fract(iTime)*2.*pi)*0.4));

    vec2 xy = fragCoord.xy / iResolution.xy;

    vec2 rf = decToPolar(fragCoord - cen);


    if (rf.x < R) {xy = (vortex(rf) + cen).xy / iResolution.xy;}

    fragColor = texture(iChannel0, xy);
}

//vec3 baseColor = vec3(0.09, 0.85, 0.94);
vec3 baseColor = vec3(0.67, 0.45, 0.96);

vec4 textured(vec2 pos){

	ivec2 P = ivec2(pos) / 1 % 1024;
    return texelFetch(iChannel0, P, 0);
}


vec4 colored(vec4 base){
    vec3 col = vec3(baseColor) * base.r * 1.4;
    return vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = colored(textured(fragCoord));
}
