vec4 HueShift (in vec3 Color, in float Shift)
{
    vec3 P = vec3(0.55735)*dot(vec3(0.55735),Color);

    vec3 U = Color-P;

    vec3 V = cross(vec3(0.55735),U);    

    Color = U*cos(Shift*6.2832) + V*sin(Shift*6.2832) + P;

    return vec4(Color,1.0);
}

float sineClampedTimescale(float t,float offset,float clampMult) {
    return clamp(t*1.5+offset,0.0,3.1415926*clampMult);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float timescale = iTime*1.2;
    float shakyTime = iTime;
    vec2 textureUv = (fragCoord.xy / iResolution.xy);
	vec2 shaky = vec2(sin(shakyTime*100.0)/100.0,cos(shakyTime*50.0)/100.0) * (sin(clamp(shakyTime*1.5+0.15,0.0,3.1415926))*.25);
    vec2 warpy = vec2(sin(uv.x*sineClampedTimescale(timescale,0.0,1.0)),sin(uv.y*sineClampedTimescale(timescale,0.0,1.0)))*0.1;
    float distanceFromCentre = smoothstep(0.25,0.75,distance(uv,vec2(0,0))+1.0-sin(sineClampedTimescale(timescale*.9,0.0,1.0))*3.0);
    vec4 theTexture = texture(iChannel0,textureUv+shaky+warpy*(1.0-sign(distanceFromCentre-0.5)));
    vec4 invertedTexture = 1.0 - theTexture;
    vec4 interpolatedTexture = mix(invertedTexture,theTexture,clamp(distanceFromCentre,0.0,1.0));
	fragColor = HueShift(interpolatedTexture.xyz,smoothstep(0.0,2.0,timescale)*0.5);
}
