#define PI 3.14159
#define FIXERROR 0.01
#define TWIST -0.2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/ iResolution.xy) - 0.5;
	uv.x *= iResolution.x/iResolution.y;

    //float d = distance(uv, vec2(cos(iTime)*0.08,sin(iTime)*0.03 ));
    float d = length(uv);

    float a = (d * TWIST) + (atan(uv.y + FIXERROR, uv.x) / (PI * 2.0));

    vec3 col = texture(iChannel0, vec2((fract(iTime * 0.1)) + a, fract(iTime * -0.1) + d)).xyz;  

    fragColor = vec4(col,1.0);
}
