void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = iResolution.xy/fragCoord-iResolution.xy+iMouse.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 90.5/cos(uv.xxx/uv.yyy*uv.yyy-iTime-uv.xyx-uv.xyx*vec3(10,12,4));

    // Output to screen
    fragColor = vec4(uv.yyy*col*iTime*uv.xyx,11.0/uv.xxx);
}
