void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;

    vec4 screen = texture(iChannel1, uv) * 100.0;
    vec4 screenprev = texture(iChannel2, uv) * 100.0;
    vec4 feedback = vec4(0.0);

    for(int i = -1; i <= 1; i++) {
        for(int j = -1; j <= 1; j++) {
            vec2 o = vec2(i, j);
            float v = length(o);
            feedback += texture(iChannel0, (uv + o / iResolution.xy - 0.5) * 1.05 + 0.5) * 0.85 * norm(v, 3.0);
        }
    }

    float matte = float(length(screen - screenprev) > length(screen) * 0.05);
	fragColor = mix(feedback, screen * 1.1, matte);
}

float norm(float v, float s) { 
    return 1.0 / sqrt(2.0 * 3.14) * exp(-0.5 * v * v / (s * s)) / s;
}

