void mainImage( out vec4 fragColor, in vec2 u)
{
    vec2 cuv = (u/iResolution.xy)*min(VIDEO_SIZE,iResolution.xy);
    float sx;
    if (iMouse.w > 0.) sx = iMouse.x;
    else sx = iResolution.x*.5;
    if (sx > u.x) fragColor = texelFetch(iChannel0,ivec2(cuv),0);
    else fragColor = texelFetch(iChannel1,ivec2(cuv),0);
}

void mainImage( out vec4 fragColor, in vec2 u )
{
    fragColor = texelFetch(iChannel0,ivec2(u),0);
}

vec4 hash(vec4 a) {
	return fract(abs(sin(a.ywxz*766.345)+cos(normalize(a)*4972.92855))*2048.97435+abs(a.wxyz)*.2735);
}

void mainImage(out vec4 fragColor, in vec2 u)
{
    int cc = int(u.x)%COMPRESSION;
    if (cc > 2) {
     	fragColor = vec4(0);
        return;
    }

    vec2 fu = vec2(floor(u.x/float(COMPRESSION))*float(COMPRESSION),u.y);
    float samples[COMPRESSION];
    for (int i = 0; i < COMPRESSION; i++) {
        ivec2 suv = ivec2(fu+vec2(i,0));
        samples[i] = texelFetch(iChannel0,suv,0)[cc]-texelFetch(iChannel2,suv,0)[cc]*KEYFRAME;
    }

    vec4 h = hash(u.xyyx+float(iFrame)*.0927),
        c = vec4(h.z*.2-.1,.1+h.x*15.,.5,.3+h.y*.2);
    int order[COMPRESSION];
	for (int i = 0; i < COMPRESSION; i++) order[i] = i;
    for (int k = 0; k < QUALITY; k++) {
        for (int i = 0; i < COMPRESSION/2; i++) {
            int ti = order[0], ii = int(fract(h.x+float(ti)*.123+float(iFrame)*.09764)*(float(COMPRESSION)-1e-4));
            order[0] = order[ii];
            order[ii] = ti;
        }
        float ps = (1.-float(k)/float(QUALITY*3/2))*PRECISION;
        for (int i = 0; i < COMPRESSION; i++) {
            int oi = order[i];
            float x = float(oi)/float(COMPRESSION-1),
                s = samples[oi],
                x1 = c.x+x*c.y,
                x2 = cos(x1),
                e = c.z+x2*c.w,
                d2 = s-e, d2p = d2*ps,
                d1 = d2*c.w*sign(cos(x1+PI*.5))*ps;
            c += vec4(d1,d1*x,d2p,d2p*x2);
        }
    }

    fragColor = c;
}

void mainImage( out vec4 fragColor, in vec2 u)
{
    if (iFrame < 10) {
     	fragColor = vec4(0,0,0,1);
        return;
    }
    ivec2 fuv = ivec2(floor(u.x/float(COMPRESSION))*float(COMPRESSION),u.y);
    float x = (floor(u.x)-float(fuv.x))/float(COMPRESSION-1);
    vec4 cr = texelFetch(iChannel0,fuv,0),
        cg = texelFetch(iChannel0,fuv+ivec2(1,0),0),
        cb = texelFetch(iChannel0,fuv+ivec2(2,0),0);
    fragColor = texelFetch(iChannel1,ivec2(u),0)*KEYFRAME+
        		vec4(cr.z+cos(cr.x+x*cr.y)*cr.w,
                     cg.z+cos(cg.x+x*cg.y)*cg.w,
                     cb.z+cos(cb.x+x*cb.y)*cb.w,0);
}
