float Band (float t, float start, float end, float blur) {
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep( end + blur, end - blur,t );
    return step1 * step2;
}

float Rectangle (vec2 uv, float blur, float left, float right, float bottom, float top) {
    float r;
    r = Band(uv.x, left, right, blur);
    r *= Band(uv.y, bottom, top, blur);
    return r;
}

float Triangle (vec2 uv, float blur, float point) {
    float t = uv.y;
    return Rectangle( uv, blur, -point + t, point -t ,-point - t , point + t);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(1.0);
    float PI = 3.14;

    uv -= vec2(0.5);
    // rotate the space
    uv = rotate2d( sin(iTime)*PI) * uv;
    // move it back to the original place
    //uv += vec2(.0,0.5);

    // Show the coordinates of the space on the background
    // color = vec3(st.x,st.y,0.0);

    // Add the shape on the foreground 
    col *=  Triangle (uv,.001, 0.17);

    fragColor = vec4(col,1.0);
}
