void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

vec4 color = vec4(0.0);
    vec4 black = vec4(0.0);
    vec4 red = vec4(1.0, 0.0, 0.0, 1.0);

    float t = iTime;

    vec2 p = fragCoord/vec2(20.0, 20.0);
    int val = int(floor(p.x-sin(t)) + floor(p.y-cos(t)));

    val = abs(val);

    if( val % 2 == 1) color = black;
    else  color = red;

    fragColor = color;
}
