vec3 plotCircle (vec2 st, vec2 center, float r, vec3 color){
    float pct = distance(st,center);
    return color + vec3(step(1.-pct, 1.-r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 st = fragCoord / iResolution.x ;
    float pct = 0.0;
    float pct2 = 0.0;

    // a. The DISTANCE from the pixel to the center
    //pct = distance(st,vec2(0.5));

    // b. The LENGTH of the vector
    //    from the pixel to the center
     //vec2 toCenter = vec2(0.5)-st;
     //pct = length(toCenter);

    // c. The SQUARE ROOT of the vector
    //    from the pixel to the center
     //vec2 tC = vec2(0.5)-st;
     //pct = sqrt(tC.x*tC.x+tC.y*tC.y);

	vec3 colorF = plotCircle(st, vec2(.5+.4*sin(iTime),0.10),0.040, vec3(0,0,1));
    colorF *= plotCircle(st, .4+.3*vec2(0.+sin(iTime),0.324+cos(iTime)),0.064, vec3(0,1,0));
    colorF *= plotCircle(st, vec2(.5,0.3),.15+.1*sin(iTime)*cos(iTime), vec3(1,0,0));
    fragColor = vec4( colorF, 1.0 );
}
