void mainImage( out vec4 fragColor, in vec2 u)
{
    vec2 res = min(VIDEO_SIZE,iResolution.xy),
        cuv = (u/iResolution.xy)*res;
    float sx;
    if (iMouse.w > 0.) sx = (iMouse.x/iResolution.x)*res.x;
    else sx = res.x*.5;

    if (sx > cuv.x) fragColor = texelFetch(iChannel1,ivec2(cuv),0);
    else {
        ivec2 fuv = ivec2(floor(cuv.x/float(COMPRESSION))*float(COMPRESSION),cuv.y);
        float x = (floor(cuv.x)-float(fuv.x))/float(COMPRESSION-1);
       	vec4 cr = texelFetch(iChannel0,fuv,0),
            cg = texelFetch(iChannel0,fuv+ivec2(1,0),0),
            cb = texelFetch(iChannel0,fuv+ivec2(2,0),0);
     	fragColor = vec4(cr.z+cos(cr.x+x*cr.y)*cr.w,
                         cg.z+cos(cg.x+x*cg.y)*cg.w,
                         cb.z+cos(cb.x+x*cb.y)*cb.w,1); 
    }
}

void mainImage( out vec4 fragColor, in vec2 u )
{
    fragColor = texelFetch(iChannel0,ivec2(u),0);
}

vec4 hash(vec4 a) {
	return fract(abs(sin(a.ywxz*766.345)+cos(normalize(a)*4972.92855))*2048.97435+abs(a.wxyz)*.2735);
}

void mainImage(out vec4 fragColor, in vec2 u)
{
    int cc = int(u.x)%COMPRESSION;
    if (cc > 2 || u.x > VIDEO_WIDTH || u.y > VIDEO_HEIGHT) {
     	fragColor = vec4(0);
        return;
    }

    vec2 fu = vec2(floor(u.x/float(COMPRESSION))*float(COMPRESSION),u.y);
    float samples[COMPRESSION];
    for (int i = 0; i < COMPRESSION; i++) samples[i] = texelFetch(iChannel0,ivec2(fu+vec2(i,0)),0)[cc];

    int hi = 4;
    vec4 c = vec4(0.,3.5,.5,.5),h;
    for (int i = 0; i < QUALITY; i++) {
        if (hi > 3) {
         	h = floor(hash((u.xyyx+float(i))*vec4(.3,.22,1.44,1.42))*(float(COMPRESSION)-1e-4))/float(COMPRESSION-1);
            hi = 0;
        }
        float pscale = 1.-float(i)/float(QUALITY*3/2),
            x = h[hi++],
            s = samples[int(x*(float(COMPRESSION)-1e-4))],
            x1 = c.x+x*c.y,
            x2 = cos(x1),
            e = c.z+x2*c.w,
            d2 = s-e, d2p = d2*pscale*PRECISION2,
            d1 = d2*c.w*sign(cos(x1+PI*.5))*pscale*PRECISION1;
        c += vec4(d1,d1*x,d2p,d2p*x2);
    }

    fragColor = c;
}
