//  colored variant of https://shadertoy.com/view/tsBXWc
// jittered variant of https://shadertoy.com/view/wdBXD3
// animated variant of https://shadertoy.com/view/tsSXD3

#define T(U) texture(iChannel0,U)                             //
void mainImage(out vec4 O, vec2 U) {
    vec2 v = T(vec3(U/360.,.1*iTime)/4.).xy-.5;
    O += .05/abs( dot( fract(U/8.+.8*T(vec3(ceil(U/8.)+.5,0)/32.).zw-.4)-.5,
                       normalize(v)))
        * ( .5 + .5 * cos( 6.3*length(v)  + vec4(0,23,21,0)  ) ); } // https://www.shadertoy.com/view/ll2cDc
