void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/min(iResolution.x, iResolution.y) * 2.;
    vec2 uva = vec2(atan(uv.x, uv.y), length(uv)*5.);
	float n = 5.;
    if (iMouse.z > 0.)
    	n = floor(iMouse.x/iResolution.x * 20.) - 10.;
	float t = iTime;
    float st = 0.2;
    if (iMouse.z > 0.)
        st = iMouse.y/iResolution.y;
    uva.x += uva.y * st;
	float lim = mix(1., 20., sin(t * 0.2) * 0.5 + 0.5);
	uva.y *= smoothstep(uva.y-lim, uva.y, sin(uva.x * floor(n))*0.5+0.5);
	uva.x /= 6.28318 / n;
    uva = abs(fract(uva)-0.5);
	uva.y += 0.5;
	fragColor.x = 1.4-max(abs(uva.x)+uva.y,-uva.y);
	fragColor.y = 1.4-length(uva);
	fragColor.z =  1.4-max(abs(uva.x),abs(uva.y));
	fragColor.w = 1.0;
}
