#define PI 3.1415926535897932384636
float normsin(float x) {return .5+.5*sin(x);}

float digauss(float x) {
   	float a=8.0;

    return pow(3.0,-a*x*x)+pow(3.0,-a*pow(x-1.0,2.0));
}


vec3 hue(float x)//not technically hue from hsv but meh its close enough
{
    x=normsin(x);
	float r=digauss(x-normsin(2.0*iTime));
    float g=digauss(x-1.0/3.0-normsin(2.0*iTime)/3.0);
   	float b=digauss(x-2.0/3.0-normsin(2.0*iTime)/3.0);
	return vec3(r,g,b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 r=(uv*uv).xy;
    fragColor=vec4(hue(5.0*pow(r.x+r.y,.5)-4.0*iTime),1);
}
