#define PI 3.14159

float random (float theta, float time) {
    //float period = mod(time, 10.0) ;
    float period = 230.000;
    return fract(sin(dot(theta, period * period))* 2.856);
}

float period(float theta, float nbCroissant) {
    float period = 2.0 * PI;
	period = period * nbCroissant;
    return sin(theta * period);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord/iResolution.xy;

    float speed = 15.0;
    float nbCroissant = mod(iTime * speed, 200.0);
    //float nbCroissant = 1.0;
    st = st * vec2(2.0) - vec2(1.0);
    float theta = atan(st.y, st.x);
    float current = (theta + PI); //[0 - 360]
    current = mod(current + PI / 2.0, 2.0 * PI);  //offset to put the beguining at the top
    current = current / (2.0 * PI); //ratio for animation

    float currentCroissant = floor(current * nbCroissant);

    float amplitude = random(currentCroissant, iTime);

    float range = period(current, nbCroissant);
    float dist = length(st);
    range = range * amplitude;

    vec3 color = dist < range ? vec3(range) : vec3(0.0);
    fragColor = vec4(color, 1.0);
}
