#define LIGHT_POS vec3(0.0, 0.0, -1.0)

const float FOVY = tan(3.14159 * 19.5 / 180.);
const int RAY_STEPS = 256;
const vec3 sphereCenter = vec3(-1.5, 4., 0.);

struct Intersection {
    float t;
    vec3 color;
    vec3 p;
    int object; // Assign every object in our scene a unique ID int
};

// SDF of a Sphere
float sphere(vec3 p, float r, vec3 c) {
    return distance(p, c) - r;
}

//SDF of a Torus
float torus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
} 

//SDF of a Cone
float cone(vec3 p, vec2 c) {
	//c must be normalized
    float q = length(p.xy);
    return dot(c, vec2(q, p.x));
}   

// SDF of a Plane
float plane( vec3 p, vec4 n ) {
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

//SDF of a Box
float box (vec3 p, vec3 b) {
	return length(max(abs(p) - b, 0.0));
}

//RANDOM Noise Functions
float random1( vec3 p ) {
    return fract(sin((dot(p, vec3(127.1,
                                  311.7,
                                  191.999)))) *         
                 43758.5453);
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p, vec2(127.1, 311.7)),
                 dot(p, vec2(269.5,183.3))))
                 * 43758.5453);
}


vec3 random3( vec3 p ) {
    return fract(sin(vec3(dot(p,vec3(127.1, 311.7, 191.999)),
                          dot(p,vec3(269.5, 183.3, 765.54)),
                          dot(p, vec3(420.69, 631.2,109.21))))
                 *43758.5453);
}

float power(float base, float exponent) {
	float result=1.0;
	for (exponent; exponent > 0.f; exponent--) {
		result = result * base;
	}
	return result;
}

float noise2D(vec2 p) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) * 43758.5453);
}

float interpNoise2D(float x, float y) {
    float intX = floor(x);
    float fractX = fract(x);
    float intY = floor(y);
    float fractY = fract(y);

    float v1 = noise2D(vec2(intX, intY));
    float v2 = noise2D(vec2(intX + 1.f, intY));
    float v3 = noise2D(vec2(intX, intY + 1.f));
    float v4 = noise2D(vec2(intX + 1.f, intY + 1.f));

    float i1 = mix(v1, v2, fractX);
    float i2 = mix(v3, v4, fractX);

    return mix(i1, i2, fractY);
}

float surflet(vec3 p, vec3 gridPoint) {
    // Compute the distance between p and the grid point along each axis, and warp it with a
    // quintic function so we can smooth our cells
    vec3 t2 = abs(p - gridPoint);
    vec3 t = vec3(1.f) - 6.f * vec3(power(t2.x, 5.f), power(t2.y, 5.f), power(t2.z, 5.f)) 
        + 15.f * vec3(power(t2.x, 4.f), power(t2.y, 4.f), power(t2.z, 4.f))  
        - 10.f * vec3(power(t2.x, 3.f), power(t2.y, 3.f), power(t2.z, 3.f));
    // Get the random vector for the grid point (assume we wrote a function random2
    // that returns a vec2 in the range [0, 1])
    vec3 gradient = random3(gridPoint) * 2.f - vec3(1,1,1);
    // Get the vector from the grid point to P
    vec3 diff = p - gridPoint;
    // Get the value of our height field by dotting grid->P with our gradient
    float height = dot(diff, gradient);
    // Scale our height field (i.e. reduce it) by our polynomial falloff function
    return height * t.x * t.y * t.z;
}


//PERLIN NOISE
float perlinNoise3D(vec3 uv) {
	float surfletSum = 0.f;
	// Iterate over the four integer corners surrounding uv
	for(int dx = 0; dx <= 1; ++dx) {
		for(int dy = 0; dy <= 1; ++dy) {
            for (int dz = 0; dz <= 1; ++dz) {
			surfletSum += surflet(uv, floor(uv) + vec3(dx, dy, dz));
            }
		}
	}
	return surfletSum + cos(iTime);
}


//FRACTAL BROWNIAN MOTION NOISE
float FBM2D(vec2 p) {
	float total = 0.f;
    float persistance = 0.5f;
    float octaves = 4.f;

    for (float i = 1.f; i <= octaves; i++) {
        float freq = power(2.f, i);
        float amp = power(persistance, i);

        total += interpNoise2D(p.x * freq + sin(iTime), p.y * freq + sin(iTime)) * amp;
    }
    return total;
}

//WORLEY NOISE 2D
float WorleyNoise2D(vec2 p) {
    // Tile the space
    vec2 pointInt = floor(p);
    vec2 pointFract = fract(p);

    float minDist = 1.0; // Minimum distance initialized to max.

    // Search all neighboring cells and this cell for their point
        for(int y = -1; y <= 1; y++)
        {
            for(int x = -1; x <= 1; x++)
            {
                vec2 neighbor = vec2(float(x), float(y));

                // Random point inside current neighboring cell
                vec2 point = random2(pointInt + neighbor);

                // Animate the point
                point = 0.5 + 0.5 * sin(iTime * 0.01 + 6.2831 * point) * sin(iTime); // 0 to 1 range

                // Compute the distance b/t the point and the fragment
                // Store the min dist thus far
                vec2 diff = neighbor + point - pointFract;
                float dist = length(diff);
                minDist = min(minDist, dist);
            }
        }
    return minDist;
}

void CoordinateSystem(in vec3 v1, out vec3 v2, out vec3 v3) {
    if (abs(v1.x) > abs(v1.y))
            v2 = vec3(-v1.z, 0.0, v1.x) / sqrt(v1.x * v1.x + v1.z * v1.z);
        else
            v2 = vec3(0.0, v1.z, -v1.y) / sqrt(v1.y * v1.y + v1.z * v1.z);
        v3 = cross(v1, v2);
}


mat3 TangentToWorld(vec3 nor) {
	vec3 tangent, bitangent;
    CoordinateSystem(nor, tangent, bitangent);
    return mat3(tangent, bitangent, nor);
}

//Rotates the camera around the Y-axis
vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

//This is where you can create your scene
#define SPHERE_1_SDF sphere(pos, 1.7, sphereCenter)
#define SPHERE_2_SDF sphere(pos, 1.0, vec3(4., 2., 0.))

//Primitive SDFs
#define BACK_WALL_SDF plane(pos, vec4(0.0, 0.0, -1.0, 6.0))
#define LEFT_WALL_SDF plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_SDF plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING_SDF plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 2.0))
#define TALL_BOX_SDF box(rotateY(pos + vec3(-2, -1.0, -2.0), 75.0 * 3.14159 / 180.0), vec3(1.2, 2.5, 1.))
#define SMALL_BOX_SDF box(rotateY(pos + vec3(1.5, 1.0, 0.0), -65.0 * 3.14159 / 180.0), vec3(1.0, 1.0, 1.0))


//Primtives IDs
#define BACK_WALL 0
#define LEFT_WALL 1
#define RIGHT_WALL 2
#define CEILING 3
#define FLOOR 4
#define TALL_BOX 5
#define SMALL_BOX 6
#define SPHERE_1 7

void sceneMap3D(vec3 pos, out float t, out int obj) {
    t = BACK_WALL_SDF;
    obj = BACK_WALL;

    float t2;
    if((t2 = LEFT_WALL_SDF) < t) {
        t = t2;
        obj = LEFT_WALL;
    }
    if((t2 = RIGHT_WALL_SDF) < t) {
        t = t2;
        obj = RIGHT_WALL;
    }
    if((t2 = CEILING_SDF) < t) {
        t = t2;
        obj = CEILING;
    }
    if((t2 = FLOOR_SDF) < t) {
        t = t2;
        obj = FLOOR;
    } 
    if ((t2 = TALL_BOX_SDF) < t) {
    	t = t2;
        obj = TALL_BOX;
    }
    if ((t2 = SPHERE_1_SDF) < t) {
    	t = t2;
        obj = SPHERE_1;
    }
}


float sceneMap3D(vec3 pos) {
    float t = BACK_WALL_SDF;

    float t2;
    if((t2 = LEFT_WALL_SDF) < t) {
        t = t2;
    }
    if((t2 = RIGHT_WALL_SDF) < t) {
        t = t2;
    }
    if((t2 = CEILING_SDF) < t) {
        t = t2;
    }
    if((t2 = FLOOR_SDF) < t) {
        t = t2;
    }  
    if ((t2 = TALL_BOX_SDF) < t) {
    	t = t2;
    }
    if ((t2 = SPHERE_1_SDF) < t) {
    	t = t2;
    }

    return t;
}

vec3 computeNormal(vec3 pos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj) {
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.0001) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

const vec3 a = vec3(0.5, 0.5, 0.5);
const vec3 b = vec3(0.5, 0.5, 0.5);
const vec3 c = vec3(2.0, 1.0, 1.0);
const vec3 d = vec3(0.5, 0.2, 0.25);

vec3 colorPalette(float t) {
    return a + b * cos(6.28 * (t * c + d));
}

// This is where you give your objects in the scene materials
vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view) {
    float lambert = dot(n, lightVec) + 0.2;

    switch(hitObj) {
        case BACK_WALL:
        return vec3(0.4, 0.7, 1.0) * lambert * colorPalette(WorleyNoise2D(p.xy));
        case SPHERE_1:
        //UV MAPPING
        vec3 d = normalize(p - sphereCenter);
        float u = 0.5f + (atan(d.z, d.x) / (2.f * 3.1459)); 
        float v = 0.5f - (asin(d.y) / 3.1459);
        return vec3(0.4, 0.8, 1.0) * lambert * colorPalette(FBM2D(vec2(u, v)));
        case CEILING:
        case FLOOR:
        return vec3(0.85, 0.81, 0.78) * lambert;
        case TALL_BOX:
        return vec3(0.3, 0.7, 1.0) * lambert * colorPalette(perlinNoise3D(p));
        break;
        case RIGHT_WALL:
        return vec3(0.065, 0.63, 0.7) * lambert;
        break;
        case LEFT_WALL:
        return vec3(0.065, 0.63, 0.7) * lambert;
        break;
        case -1:
        return vec3(0., 0., 0.);
        break;
    }
    return vec3(0., 0., 0.);
}


Intersection sdf3D(vec3 dir, vec3 eye) {
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);
    vec3 lightDir = normalize(LIGHT_POS - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}

// Returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc) {
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0,1,0)));
    vec3 U = normalize(cross(R, F));

    // Vertical and Horizontal Aspects
    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    // Convert to range [-1, 1]
    uv = uv * 2.0 - vec2(1.0, 1.0);

    //This is with the rotating part
    //vec3 eye = rotateY(vec3(0, 0, -10), iTime);

    vec3 eye = vec3(0, 4.5, -30);
    vec3 ref = vec3(0, 2.8, 0);

    vec3 rayDir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(rayDir, eye);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    // fragColor = vec4(0.5 * (rayDir + vec3(1., 1., 1.)), 1.0);
    fragColor = vec4(isect.color, 1.);
}
