void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
       // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    int numCentroids = 100;
    float inverseSpeed = 1000.0f;

    float minDist = 2.0f;
    int idx = 0;
    for(int i = 0; i < numCentroids; ++i)
    {
    	vec2 centLookup = vec2(float(i) / float(numCentroids)) * 1.0f;
        centLookup.x += (iTime / inverseSpeed);
        vec2 cent = texture(iChannel0, centLookup).rg;
        vec2 offset = cent - uv;
        float dist = dot(offset, offset);
        if(dist < minDist)
        {
            minDist = dist;
            idx = i;
        }
    }


    // Output to screen
    float col = float(idx) / float(numCentroids);
    col = minDist * 100.0;
    //col = 0.0005 / minDist;

    vec2 centLookup = vec2(float(3) / float(numCentroids));
    vec3 cent = texture(iChannel0, centLookup).rgb;
    //fragColor = vec4(cent.r, cent.g, cent.r, 1.0);
    fragColor = vec4(col, col, col, 1.0);
}
