vec3 CamPos = vec3(2.0,0.0,0.0);
vec3 LookAt = vec3(0.0,0.0,0.0);
vec3 CamAng = vec3(0.0,0.0,0.0);
vec3 SpherePos = vec3(0.0,0.0,0.0);
vec3 LightPos = vec3(0.0,5.0,0.0);
float SphereRad = 1.0;



float checkCollide(vec3 Origin, vec3 rayDir){
    vec3 L = (SpherePos-Origin);
    float tca = dot(L,rayDir);


    if(tca<0.0){
        return -1.0;
    }

    float d = sqrt(dot(L,L)-(tca*tca));

    if(d>SphereRad){
        return -1.0;
    }

    float thc = sqrt((SphereRad*SphereRad)-(d*d));
    float t0= tca-thc;
    return t0;

}

bool checkShadow(vec3 P){
    vec3 ray = LightPos-P;
    vec3 rayDir = ray/length(ray);


    vec3 L = (SpherePos-P);
    float tca = dot(L,rayDir);

    float d = sqrt(dot(L,L)-(tca*tca));

    if(d==SphereRad || tca<0.0){
        return false;
    }
    return true;
}


int castRay(vec2 xy){
    float ratio = CamAng.z/length(CamAng.xy);
    vec3 VertPerp = vec3(-CamAng.x*ratio, -CamAng.y*ratio, length(CamAng.xy));
    vec3 yComp = VertPerp * xy.y;

    vec3 HoriPerp = vec3(CamAng.y,-CamAng.x,0.0)/length(vec3(CamAng.y,-CamAng.x,0.0));
    vec3 xComp = HoriPerp * xy.x;

    vec3 ray = (CamAng*3.0)+xComp+yComp;
    vec3 rayDir = (ray/length(ray));

    float t1 = checkCollide(CamPos,rayDir);
    if(t1>0.0){
        vec3 P1 = CamPos + (rayDir*t1);
       	if(checkShadow(P1)){
            return 2;
        }
        return 1;
    }
    return 0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    SpherePos = vec3(0.0,sin(2.0*iTime),0.0);
    //LookAt= SpherePos;
    LightPos = vec3(0.0,0.0,5.0);
    CamPos = vec3(6,0.0,0.0);
    CamAng = LookAt-CamPos;
    //CamAng = vec3(0.0,-1.0,-1.0);
    CamAng = CamAng/length(CamAng);




    vec2 xy = vec2((2.0*(iResolution.x/iResolution.y)) * fragCoord.x/iResolution.x - (iResolution.x/iResolution.y), 2.0 * fragCoord.y/iResolution.y - 1.0);
    //vec2 xy = 2.0 * fragCoord/iResolution.xy - 1.0;
    vec3 col = vec3(0.5,0.5,0.5);

    int rayColor = castRay(xy);

    if(rayColor==1){
        col = vec3(1.0,0.0,0.0);
    }
    else if(rayColor==2){
        col = vec3(0.0,0.0,0.0);
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
