// variant of https://shadertoy.com/view/3dyfWV

#define V(X,Y)  step( .8-.7*length( ceil(U-R/z/2.) +Y ) *z/R.y       , \
                      texelFetch( iChannel0, ivec2(U+vec2(X,Y)), 0 ).x )

void mainImage( out vec4 O, vec2 u )
{
    float z = 2.;  // iResolution.y/180.; // if you want exact zoom
    vec2 U = u/z, R = iResolution.xy,
         I = round(U), F = U-I, S = step(0.,F); 

    O += V(0.,0.) -O;  if ( iMouse.z > .5 ) return; 

    F = abs(F); U = --I; 
    float x = S.x, y = S.y,
          a = V(1.-x,y), b = V(x,y);

    O = a == V(x,1.-y) && a != b && ( b>0. || b != V(1.-x,1.-y) )
        ? mix( O, 1.-O, clamp(1.-F.x-F.y,0.,1.)  ) : O;
}
