#define pi 3.14159265359


const float speed = 4.0;
const float interval = 1.0;

const float line_width = 0.005;
const float line_glow_width = 0.025;


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float time = mod(iTime, interval + speed) - interval;
    time *= step(0., time);
    time /= speed;

    bool change_scene = int(iTime/(interval+speed)) % 2 == 0;

    vec2 uv = fragCoord/iResolution.xy;
    if (uv.y > 1.-time) {
        change_scene = !change_scene;
    }
    vec3 col = (change_scene)
        ? texture(iChannel0, uv).rgb 
        : texture(iChannel1, uv).rgb;

    fragColor = vec4(col,1.0);
}
