void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv*=4.;

    uv.y /= 16./9.;

    float xx = uv.y;
    uv.y = uv.x;
    uv.x = xx;

    int lights =400;
    float timespeed = 240.;


    float t = float(int(iTime*timespeed))/float(lights);
    float x = float(int(uv.x*float(lights)))/float(lights);



     float fil = step(0.95,mod(iTime*2.*3.14+atan(uv.x,uv.y)*3.14*3.,59.15/20.));
    uv.x = -abs(uv.x)-fil;
    float dist = sqrt(uv.x*uv.x+uv.y*uv.y);

    float r = step(0.90+0.08*sin(3.14/2.+t*2.),sin((mod(3./4.+atan(dist),1.))*2.*3.14+sin(t*2.)*2.));
    r = step(0.,r)*r;


    float g = 0.;
    //float g = sin((3./4.-x)*2.*3.14+sin(t*2.)*2.);

    float b = 0.;//abs(1.-2.*mod(1.+mod(uv.x*2.,1.)+time,1.));

    vec3 col = vec3(1.,1.,1.)*r;
   	col += 0.8*(vec3(1.,1.,1.)-col)*vec3(sin(uv.x*uv.y),cos(uv.y*uv.x),cos(iTime+uv.y*uv.x));

    col*=vec3(sin(iTime+x),cos(iTime+x*uv.y),1.);

    //col *= step(uv.y,0.55)*step(0.45,uv.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}
