void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).rgb;
    fragColor = vec4(col,1.0);
}

#define T(x,y) texture(iChannel0, tv+vec2(x,y)).rgb
//#define iTime (iTime)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = -1. + 2. * uv;
    vec2 R = iResolution.xy;
    vec2 tv = fragCoord/iResolution.xy;;
    vec2 px = vec2(1./R.x,1./R.y)*2.;
    vec3 c = T(0.,0.);
    px *= (1.-c.r)*c.r*4.;
    vec2 fc = fragCoord.xy;
    vec3 col = vec3(0.);
    vec3 u = T(0,px.y);
    vec3 d = T(0.,-px.y);
    vec3 l = T(px.x,0.);
    vec3 r = T(-px.x,0.);

    vec3 udlr = (u+d+l+r)/4.;
    if (tv.x < .05 || tv.y < .05 || tv.x > 0.95 || tv.y > 0.95) {
        col.r = sin(uv.x+uv.y+iTime*0.1)*.5+0.5;
    }
    if (u.r > d.r || d.r > u.r) {
        col.b = ((u.r-d.r))*(500.);
    }
    if (l.r > r.r || l.r > r.r) {
        col.g = ((l.r-r.r))*(500.);
    }
    vec3 c2 = T((col.b-0.5)*px.x,(col.g-0.5)*px.y);
    col.r += (u.r+d.r+l.r+r.r)/4.3;
    col.r = mod(col.r+0.03,sin(iTime*0.1)*0.5+.75)*0.96;
    col.r += c2.r*0.2;
    col.b *= 0.001;
    col.g *= 0.01;
    col = mod(col*0.9,1.);
    col = mix(col,(udlr+c)/2.,col.b-col.r*0.95);
    //col = mix(col,c,0.9);
    fragColor = vec4(col, 1.);
}
