#define PI 3.14159265358979323846

vec2 random2D(vec2 st){
  st=vec2(dot(st,vec2(127.1,311.7)),dot(st,vec2(269.5,183.3)));
  return-1.+2.*fract(sin(st)*43758.5453123);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st){
  vec2 i=floor(st);
  vec2 f=fract(st);
  // mixing function
  vec2 u=f*f*(3.-2.*f);

  return mix(mix(dot(random2D(i+vec2(0.,0.)),f-vec2(0.,0.)),
  dot(random2D(i+vec2(1.,0.)),f-vec2(1.,0.)),u.x),
  mix(dot(random2D(i+vec2(0.,1.)),f-vec2(0.,1.)),
  dot(random2D(i+vec2(1.,1.)),f-vec2(1.,1.)),u.x),u.y);
}

float circle(in vec2 _st,in float _radius){
  vec2 l=_st-vec2(.5);
  return 1.-smoothstep(_radius-(_radius*.01),
  _radius+(_radius*.01),
  dot(l,l)*4.);
}

float shape(vec2 st,float radius){
  st=st-vec2(.5);
  float r=length(st)*2.;
  float a=atan(st.y,st.x);
  float m=abs(mod(2.*iTime,2.*PI)-PI)/a*2.2;
  float f=radius;
  m+=noise(st+iTime*.1)*.5;

  f+=sin(a*100.)*noise(st+iTime*.2)*.1;
  f+=(sin(a*100.)*.1*pow(m,2.));
  return 1.-smoothstep(f,f+.007,r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 st=fragCoord.xy/iResolution.y;
  vec3 color=vec3(0.);
  color=vec3(shape(st,.5));

  fragColor=vec4(color,1.);
}
