#define RES vec2(200, 100)
#define BORDER vec2(0.1, 0.1)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * RES;
    vec2 p = fract(uv);
    float barW = (1.0 - 2.0 * BORDER.x) / 3.0;
    vec3 rgb = step(
        vec3(p.x, BORDER.x + barW, BORDER.x + 2.0 * barW),
        vec3(BORDER.x + barW, p.x, p.x)
    );
    rgb.g -= rgb.b;
    vec3 tex = texture(iChannel0, floor(uv) / RES).rgb;
    vec2 mask = step(BORDER, p) * step(p, 1.0 - BORDER);
    fragColor = vec4(rgb * tex * mask.x * mask.y,1.0);
}
