void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed_1 = 1.;
    float speed_2 = 0.5;

    /////////////////

    float ratio = sin(iTime*speed_1) * 0.05;

    /////////////////

    float amt = ratio;
    float warpDistance = ratio;


    vec2 uv = fragCoord/iResolution.xy;
    vec3 d = texture(iChannel1, uv).rgb;
    vec3 luma = vec3(0.299, 0.587, 0.114);
    float power = dot(d, luma);

    power = sin(3.1415927*2.0 * mod(power + iTime * .25, 1.0));

  //  vec3 d = texture(iChannel1, uv).rgb;
 	d *= amt;

	vec3 e = texture(iChannel0, uv+vec2(0, power)*warpDistance + d.r).rgb;
    vec3 f = texture(iChannel0, uv+vec2(0, power)*warpDistance + d.g).rgb;
    vec3 g = texture(iChannel0, uv+vec2(0, power)*warpDistance + d.b).rgb;
    vec3 h = vec3(e.r,f.g,g.b);

    fragColor = vec4(h,1.);
}
