float inverseLerp(float minVal, float maxVal, float val)
{
    return (val - minVal) / (maxVal - minVal);
}

float fit(float inVal, float inMinVal, float inMaxVal, float outMinVal, float outMaxVal)
{
    float inNorm = inverseLerp(inMinVal, inMaxVal, inVal);
    float outRange = outMaxVal - outMinVal;
    float outVal = (inNorm * outRange) + outMinVal;
    return outVal;
}

float rand(float x)
{
    return fract(sin(x) * 1346.334);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ////////////////
    int seedX = 345;
    int seedY = 546;
    int numPoints = 1700;
    float speed = 0.05;
    float pointRadius = 0.06;
    vec3 backColor = vec3(0.1, 0.8, 0.75);
    vec3 pointColor = vec3(0.4, 0.1, 0.9); 
    ///////////////

    vec2 uv = (fragCoord / iResolution.xy);

    float minDist = 10000.0;

    for(int i = 0; i < numPoints; i++)
    {
        float xPos = rand(float(i+seedX)) + rand(float(i+5))*sin((rand(float(i+seedX)))*(250.0+iTime)*speed);
        float yPos = rand(float(i+seedY)) + rand(float(i+1))*cos((rand(float(i+seedY)))*(251.0+iTime)*speed);
        vec2 point = vec2(xPos, yPos);
        minDist = min(length(uv-point), minDist);
    }

    minDist = fit(minDist, 0.0, pointRadius, 0.0, 1.0);

    vec3 color = backColor;
    color = mix(color, pointColor, 1.0 - minDist);
    fragColor = vec4(color, 0.0);
}
