void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 col;
    float t = .5;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y;
    uv /= 5.0+sin(iTime);
    uv += vec2(t*-1.0,t*1.65);
    float factor = 1.5;
    vec2 v1;
    for(int i=0;i<12;i++)
    {
        uv *= -factor*factor;
        v1 = uv.yx/factor;
        uv += sin(v1+col)/factor;
        col += vec2(sin(uv.x-uv.y+v1.y-col.y),sin(uv.y-uv.x+v1.x-col.x));
        factor -= .05;
    }
    fragColor = vec4(vec3(col.x+4.0,col.x-col.y/2.0,col.x/5.0)/4.0,1.0);
}
