vec3 mod3(vec3 x) {return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec2 mod2(vec2 x) {return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec3 permute(vec3 x) {	return mod3(((x*34.0)+1.0)*x);}

float snoise(vec2 pos){
	const vec4 C = vec4(0.2113, 0.3660,  -0.5773, 0.0243);
	//const vec4 C=vec4(0.,.3660,-.5773,.0243);// светотень
	vec2 i  = floor(pos + dot(pos, C.yy) );
	vec2 x0 = pos -   i + dot(i, C.xx);
	vec2 i1;
	i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
	vec4 x12 = x0.xyxy + C.xxzz;
	x12.xy -= i1;
	i = mod2(i);
	vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 )) + i.x + vec3(0.0, i1.x, 1.0 ));
	vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
	m = m*m ;
	m = m*m ;
	vec3 x = 2.0 * fract(p * C.www) - 1.0;
	vec3 h = abs(x) - 0.5;
	vec3 ox = floor(x + 0.5);
	vec3 a0 = x - ox;
	m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
	vec3 g;
	g.x  = a0.x  * x0.x  + h.x  * x0.y;
	g.yz = a0.yz * x12.xz + h.yz * x12.yw;
	return 130.0 * dot(m, g);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord) {
	vec2 uv = fragCoord.xy/iResolution.xy;
	vec4 img = texture(iChannel0, uv);
	vec2 pos = ( fragCoord.xy + iTime*1.2);
	float color = snoise(pos /4000.0) * .9 + snoise(pos / 6.0) * 0.53 + snoise(pos * 1.0) * 0.342;
    //vec4 out_color=vec4((1.-buf));
    vec4 out_color=vec4(1.);
    out_color.xy*=img.x*color;
    out_color.xz*=img.y*color;
    out_color.yz*=img.z*color;
	//fragColor = vec4( vec3( color ), 1.0 );
	fragColor=vec4(out_color*color)*15.;
}
