#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
void mainImage( out vec4 Q, in vec2 U )
{
    Q = 0.5+0.5*A(U);
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 m = 0.25*(A(U+vec2(0,1))+A(U-vec2(0,1))+A(U+vec2(1,0))+A(U-vec2(1,0)));

    Q.y -= .5*(m-Q-.4*(dot(Q.xy,Q.xy)*Q-Q)).x;


	if (iFrame < 1) Q.x = 1.;
	if (iMouse.z>0.&&length(U-iMouse.xy)<10.||(iFrame < 1&&length(U-0.5*R)<10.)) 
        Q.xy=normalize(exp(-.01*dot(U-iMouse.xy,U-iMouse.xy))*vec2(sin(10.*iTime+U.x),cos(10.*iTime+U.x))+vec2(1,0));

}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 m = 0.25*(A(U+vec2(0,1))+A(U-vec2(0,1))+A(U+vec2(1,0))+A(U-vec2(1,0)));

    Q.x += .5*(m-Q-.4*(dot(Q.xy,Q.xy)*Q-Q)).y;




	if (iFrame < 1) 
        Q.xy=normalize(exp(-.01*dot(U-0.5*R,U-0.5*R))*vec2(sin(10.*iTime+U.x),cos(10.*iTime+U.x))+vec2(1,0));

    if (iMouse.z>0.&&length(U-iMouse.xy)<10.) 
        Q.xy=normalize(exp(-.01*dot(U-iMouse.xy,U-iMouse.xy))*vec2(sin(10.*iTime+U.x),cos(10.*iTime+U.x))+vec2(1,0));

}
