void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    uv *= 3.; // zoom out

    float w = 5./iResolution.y;

    float sun = length(vec2(0)-uv);
    sun = smoothstep(1., 1.-w, sun);

    float waves = sin(uv.y*50.+3.5)*.5+.5;
    float a = max(.1, mix(.75, 5., (uv.y)/4.));
    waves = smoothstep(a, a-w, waves);
    waves *= sun;

    vec3 col = vec3(mix(sun, waves, step(0., -uv.y)));
    col *= vec3(1., .65 + uv.y*.5, .5);

    // Output to screen
    fragColor = vec4(col,1.0);
}
