//  Wave displacement line segments with polar forces
void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    vec4 d = D(U);
    vec2 l = ln(U,a.xy,a.zw);
    Q = (0.5+0.1*sin(.1*a.x+vec4(1,2,3,4)))+15.*vec4(-d.x,0,d.x,1);
    l.y = l.y*2.-1.;
    Q *=1.-smoothstep(2.,1.,l.x)*vec4(-l.y,1,l.y,1);

    Q *= 1.-0.5*texture(iChannel2,normalize(vec3(d.zw,1)));

}

#define R iResolution.xy
//Texture lookups :
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
// Blur iterations, should be at least 2*std
#define I 15.
// Force strength and blur
#define f1 3e3
#define f2 6.
#define std vec4(6,4,1,1)
// Distance to line
vec2 ln (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	return vec2(length(p-a-(b-a)*i),i);
}

// Segment Distance Sort & Advection
void X (inout vec4 Q, vec2 U, vec2 r) {
    vec4 n = A(U+r);
	if (ln(U,n.xy,n.zw).x<ln(U,Q.xy,Q.zw).x) Q = n;
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);

    X(Q,U,vec2(1,0));
    X(Q,U,vec2(0,1));
    X(Q,U,vec2(-1,0));
    X(Q,U,vec2(0,-1));
    X(Q,U,vec2(2,2));
    X(Q,U,vec2(-2,2));
    X(Q,U,vec2(-2,-2));
    X(Q,U,vec2(2,-2));


    Q.xy -= D(Q.xy).zw;
    Q.zw -= D(Q.zw).zw;

    vec2 d = (Q.zw-Q.xy);
    float l = length(d);
    if (l >0.) {
    	Q.xy += .5*normalize(d)*(l-14.);
    	Q.zw -= .5*normalize(d)*(l-14.);
    }



    if (iMouse.z>0.) {
        vec4 n = B(vec2(0));
    	if (length(n.xy-n.zw)>0.&&ln(U,n.xy,n.zw).x<ln(U,Q.xy,Q.zw).x) Q = n;
    }

    if (iFrame < 1) {
    	Q = vec4(.49*R,.51*R);
    }
}

//Mouse
void mainImage( out vec4 C, in vec2 U )
{
    vec4 p = texture(iChannel0,U/iResolution.xy);
   	if (iMouse.z>0.) {
      if (p.z>0.) C =  vec4(iMouse.xy,p.xy);
    else C =  vec4(iMouse.xy,iMouse.xy);
   }
    else C = vec4(-iResolution.xy,-iResolution.xy);
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = vec4(0);
    for (float i = -I; i <= I; i++) {
        vec2 x = U+vec2(i,0);
        vec4 b = A(x);
        vec2 l = ln(x,b.xy,b.zw);
        vec4 s = exp(-l.x)*vec4(l.y*2.-1.,1,0,0);
    	Q += s*0.3989422804/std*exp(-.5*i*i/std/std);
    }
}

void mainImage( out vec4 Q, in vec2 U )
{
    vec4 
        d = D(U),
        n = D(U+vec2(0,1)),
        e = D(U+vec2(1,0)),
        s = D(U-vec2(0,1)),
        w = D(U-vec2(1,0));
    Q = d*.55;
    for (float i = -I; i <= I; i++) 
    	Q += 0.3989422804/std*A(U+vec2(0,i))*exp(-.5*i*i/std/std);


    Q.zw  = f1*d.x*vec2(e.x-w.x,n.x-s.x);
    Q.zw += f2*vec2(e.y-w.y,n.y-s.y);
}
