// x-axis is green
// y-axis is red
// space origin is blue

#define PI 3.141592653589793238


vec2 rotate2d(vec2 uv, float angle) {
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * uv;
}

void mainImage(out vec4 color, in vec2 coord) {
    vec3 col;

    vec2 uv = 2.0*(coord-0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy - 0.5*iResolution.xy)/iResolution.xy;
    uv = rotate2d(uv, atan(mouse.y, mouse.x) + 3.0*PI/4.0);

    float d;
    uv.x = abs(uv.x);
    uv.y = uv.y*1.4 + pow(uv.x - 0.5, 2.0);
    d = length(uv) - 0.3 + pow(0.5 + 0.5*sin(iTime*10.0), 4.0)*0.1;

    float linewidth = 4.0/iResolution.y;
    col += smoothstep(linewidth, 0.0, d) * 0.5;
    col.rg += smoothstep(-0.5, 0.5, sin(uv*100.0)) * 0.1;
    col.r += smoothstep(linewidth, 0.0, length(uv.x))*0.5;
    col.g += smoothstep(linewidth, 0.0, length(uv.y))*0.5;
    col.b += smoothstep(linewidth, 0.0, pow(length(uv), 1.4));

    color = vec4(col,1);
}
