void mainImage( out vec4 fragColor, in vec2 fragCoord )  
{
    float PI = 3.14159265;
	//*sin(iTime+PI/2.)
    vec2 c1 = vec2(iResolution.x*0.33,iResolution.y*0.33);
    vec2 c2 = vec2(iResolution.x*0.66,iResolution.y*0.33);
    vec2 c3 = vec2(iResolution.x*0.5,iResolution.y*0.66);
    float c1D = 1. - distance(fragCoord,c1+cos(iTime*0.2)*(iResolution.xy/5.))/(iResolution.x*0.66);
    float c2D = 1. - distance(fragCoord,c2+sin(iTime*0.3+PI/4.)*(iResolution.xy/5.))/(iResolution.x*0.66);
    float c3D = 1. - distance(fragCoord,c3+sin(iTime)*(iResolution.xy/5.))/(iResolution.x*0.66);
    vec3 col = vec3(c1D,c2D,c3D);
    fragColor = vec4(col,0.0);
}
