float rand(float x)
{
    return fract(sin(dot(vec2(x),vec2(12.368,3.1442))))*18.96;
}


float noise(float x)
{
    float i=fract(x);
    return rand(i);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 xy = fragCoord/iResolution.xy;

   // mat2 translate=mat2(cos(iTime),-sin(iTime),
                        //sin(iTime), cos(iTime));
   // mat2 translate=mat2(sin(iTime),0,
                         //0,sin(iTime));
    mat2 translate=mat2(sin(iTime*xy.x),0,
                         0,sin(iTime*xy.x));
    xy*=translate*3.;
    float y=noise(xy.x);
    vec3 color=vec3(y);
   fragColor=vec4(color,1.);

}
