const float N = 6.;
const float m = 0.007;

const float period = 3.;

float rand(vec2 co, float seed){
    co.x += seed + 0.14;
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float line( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    float d = length( pa - ba*h );
    return 1. - smoothstep(abs(d), 0., m);
}

float remap(float x, float a1, float b1, float a2, float b2) {
    return a2 + (b2 - a2) * (x - a1) / (b1 - a1);
}

float tile(vec2 uv) {
    float t = 0.005;
    float time = pow(abs(remap(sin(iTime*6.28 / period - 3.14), -1., 1., 0.001, 1.)), 15.);
    float p = mix(0., -.55, time);
    t += line(uv, vec2(-0.5, -p), vec2(p, 0.5));
    t += line(uv, vec2(-p, -0.5), vec2(0.5, p));
    uv.y *= -1.;
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.y;
    //uv.x -= ratio*0.5;
    //
    vec3 col = vec3(0.);
    if (abs(uv.x-ratio*0.5) < .5) {
        uv.x -= (ratio - 1.) * 0.5;
        //
        vec2 guv = fract(N * uv) - 0.5;
        vec2 gid = floor(N * uv);
        int rand = int(floor(rand(gid, 0.123 * mod(floor(iTime / period + 0.25), 5.))*4.));
        if (rand == 0) {

        }
        else if (rand == 1) {
            guv = vec2(guv.y, -guv.x);
        }
        else if (rand == 2) {
            guv *= -1.;
        }
        else if (rand == 3) {
            guv = vec2(-guv.y, guv.x);
        }
        col += tile(guv);
    }
    else {
     col = vec3(0.3);   
    }
    //
    fragColor = vec4(col,1.0);
}
