void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy) / (iResolution.y);
    float wave_y = uv.y*.5 + .5;
    uv = mod(uv,1.);
    uv -= .5;
	float beat = texture(iChannel0, vec2(.5,.25)).r;
    float wave = texture(iChannel0, vec2(wave_y,.75)).r;
    //float circle = step((0. + beat)/5.,length(uv));
    uv.x += (wave*2. - 1.)/3.;
    float circle = step((.3 + beat)/3.,length(uv));
    uv.x = abs(uv.x/.5);
    float stick = step(.08*wave, uv.x);
	float col = min(stick, circle);
    fragColor = vec4(vec3(col), 1.);
}
