uniform sampler2D u_Drip;

vec3 czm_saturation(vec3 rgb, float adjustment){
 	const vec3 W = vec3(0.2125, 0.7145, 0.0721);
    vec3 intensity = vec3(dot(rgb, W));
    return mix(intensity, rgb, adjustment);                      
}

vec3 hueShift(vec3 color, float hue){
    vec3 k = vec3(0.57735);
    float cosAngle = cos(hue);
    return vec3(color * cosAngle + cross(k, color) * sin(hue) + k * dot(k, color) * (1.0-cosAngle));

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	//uv = (uv-0.5)*2.;
    //uv.x *= iResolution.x/iResolution.y;
   	//uv = uv*iChannelResolution[0].xy;

    uv.x += floor(iTime * 0.0025 + 3.0 * uv.x) * 0.05;
	 uv.x = mod(uv.x, 1.);

    float drip = texture(u_Drip, uv).r;
    vec3 color = texture(iChannel0, uv).rgb;

    color = hueShift(color, iTime * 0.001 + 10. * uv.y);
    color.g *= 1.1;

    float lumin = dot(color,vec3(1.0))/3.;
    color *= lumin;
    color += color;
    // sample texture and output to screen
    color = czm_saturation(color, drip * 15. * lumin);


    //c.xyz = czm_saturation(c.xyz, sin(iTime));
    //c.xyz = hueShift(c.xyz, cos(iTime) * 0.001 + 10. * iChannelResolution[0].y);

    fragColor = vec4(color, 1.);
}
