void Spherize(vec2 UV, vec2 Center, float Strength, vec2 Offset, out vec2 Out)
{
    vec2 delta = UV - Center;
    float delta2 = dot(delta.xy, delta.xy);
    float delta4 = delta2 * delta2;
    vec2 delta_offset = vec2(delta4 * Strength);
    Out = UV + delta * delta_offset + Offset;
}
vec2 FishEye(vec2 uv, float str )
{
    vec2 p = (uv - 0.5) * 2.0;		

    vec2 offset;
    offset.x = ( pow(p.y,2.0)) * str * (p.x);
    offset.y = ( pow(p.x,2.0)) * str * (p.y);

    return uv + offset;	     
}
void CRT(in vec2 UV,in vec3 Color,float power,vec3 scancolor,out vec3 Result)
{
	float apply = abs(sin(UV.y)*power);    
    Result=mix(Color,scancolor,apply);
}
void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
	float scan =0.5;
	vec2 uv = fragCoord/iResolution.xy;
    vec3 Result;
    vec2 p;
    Spherize(uv,vec2(0.5),-0.6,vec2(0.0),p);
	CRT(fragCoord,texture(iChannel0,p).rgb,scan,vec3(0.0),Result);	
    fragColor=vec4(Result,1.0);
}
