renderpass.code:
vec3 sand=vec3(0.8,0.76,0.63);
vec3 cha=vec3(0.75,0.58,0.4);
vec3 green=vec3(0.19,0.32,0.03);
vec2 gradientNoise_dir(vec2 uv)
{
	vec2 p=vec2(int(uv.x)%289,int(uv.y)%289);
	float x = (34. * p.x + 1.) * float(int(p.x) % 289) + p.y;
	x = (34. * x + 1.) * float(int(x) % 289);
	x = fract(x / 41.) * 2. - 1.;
	return normalize(vec2(x - floor(x + 0.5), abs(x) - 0.5));
}
float gradient_noise(vec2 p)
{
	vec2 ip = floor(p);
	vec2 fp = fract(p);
	float d00 = dot(gradientNoise_dir(ip), fp);
	float d01 = dot(gradientNoise_dir(ip + vec2(0., 1.)), fp - vec2(0., 1.));
	float d10 = dot(gradientNoise_dir(ip + vec2(1., 0.)), fp - vec2(1., 0.));
	float d11 = dot(gradientNoise_dir(ip + vec2(1., 1.)), fp - vec2(1., 1.));
	fp = fp * fp * fp * (fp * (fp * 6. - 15.) + 10.);
	return mix(mix(d00, d01, fp.y), mix(d10, d11, fp.y), fp.x);
}
void GradientNoise(vec2 UV, float Scale, out float Out)
{
	Out = gradient_noise(UV * Scale) + 0.5;
}
float Posterize(float In, float Steps)
{
   return floor(In/(1./Steps)) *(1./Steps);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  (2.*fragCoord -iResolution.xy)/iResolution.y;
    float a,b;
    GradientNoise(uv,4.,a);
    GradientNoise(uv+vec2(0.0,0.3),3.2,b);

    a=Posterize(a,2.8);
    b=Posterize(b,2.8);
    vec3 color;
    sand*=a;
    green*=b;
    vec3 col;
    col=mix(green,sand,0.5);
    fragColor = vec4(col,1.0);
}
